        }
        $log.warn('no state params available fo page fallCreation, this is probably a bug');
        return $q.defer(gesuchModelManager.getGesuch());
    }
    exports.reloadGesuchModelManager = reloadGesuchModelManager;
    getKinderDubletten.$inject = ['$stateParams', '$q', '$log', 'KindRS', 'AuthServiceRS'];
    /* @ngInject */
    // Die Kinderdubletten werden nur für JA-Mitarbeiter (inkl. Revisor und Jurist) angezeigt
    function getKinderDubletten($stateParams, $q, $log, KindRS, authService) {
        var isAdmin = authService.isOneOfRoles(TSRoleUtil_1.TSRoleUtil.getJugendamtRole());
        if (isAdmin && $stateParams && $stateParams.gesuchId) {
            var gesuchIdParam = $stateParams.gesuchId;
            return KindRS.getKindDubletten(gesuchIdParam);
        }
        var deferred = $q.defer();
        deferred.resolve(undefined);
        return deferred.promise;
    }
    exports.getKinderDubletten = getKinderDubletten;
    createEmptyMutation.$inject = ['GesuchModelManager', '$stateParams', '$q'];
    function createEmptyMutation(gesuchModelManager, $stateParams, $q) {
        if ($stateParams) {
            var gesuchId = $stateParams.gesuchId;
            var eingangsart = $stateParams.eingangsart;
            var gesuchsperiodeId = $stateParams.gesuchsperiodeId;
            var fallId = $stateParams.fallId;
            if (gesuchId && eingangsart) {
                gesuchModelManager.initMutation(gesuchId, eingangsart, gesuchsperiodeId, fallId);
            }
        }
        return $q.defer(gesuchModelManager.getGesuch());
    }
    exports.createEmptyMutation = createEmptyMutation;
    createEmptyErneuerungsgesuch.$inject = ['GesuchModelManager', '$stateParams', '$q'];
    function createEmptyErneuerungsgesuch(gesuchModelManager, $stateParams, $q) {
        if ($stateParams) {
            var gesuchId = $stateParams.gesuchId;
